import React from 'react';
import Navigation from './Navigation';
import './style.css';

const DEFAULT_MOOD = 'happy';

class Contact extends React.Component {
    state = {
        mood: DEFAULT_MOOD
    };

    componentDidMount() {
        this.setState({mood: DEFAULT_MOOD});
    }

    onClick = () => {
        if (this.state.mood !== 'shocked') {
            this.setState({mood: 'shocked'});
        } else {
            this.setState({mood: DEFAULT_MOOD});
        }
    }

    onMouseEnter = () => {
        this.setState({mood: 'blissful'});
    }

    onMouseLeave = () => {
        this.setState({mood: DEFAULT_MOOD});
    }

    render() {
        return (
            <div>
                <Navigation />
                <div className="app">
                    <div className="ui list">
                        <div className="item">
                        <img className="ui avatar image" src="/images/avatar2/small/rachel.png" />
                        <div className="content">
                            <a className="header">Rachel</a>
                            <div className="description">Last seen watching <a><b>Arrested Development</b></a> just now.</div>
                        </div>
                        </div>
                        <div className="item">
                        <img className="ui avatar image" src="/images/avatar2/small/lindsay.png" />
                        <div className="content">
                            <a className="header">Lindsay</a>
                            <div className="description">Last seen watching <a><b>Bob's Burgers</b></a> 10 hours ago.</div>
                        </div>
                        </div>
                        <div className="item">
                        <img className="ui avatar image" src="/images/avatar2/small/matthew.png" />
                        <div className="content">
                            <a className="header">Matthew</a>
                            <div className="description">Last seen watching <a><b>The Godfather Part 2</b></a> yesterday.</div>
                        </div>
                        </div>
                        <div className="item">
                        <img className="ui avatar image" src="/images/avatar/small/jenny.jpg" />
                        <div className="content">
                            <a className="header">Jenny Hess</a>
                            <div className="description">Last seen watching <a><b>Twin Peaks</b></a> 3 days ago.</div>
                        </div>
                        </div>
                        <div className="item">
                        <img className="ui avatar image" src="/images/avatar/small/veronika.jpg" />
                        <div className="content">
                            <a className="header">Veronika Ossi</a>
                            <div className="description">Has not watched anything recently</div>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Contact;
